const initialState={
    pokemons : [],
    allPokemons:[],
    types: []
}

const rootReducer= (state=initialState,action)=>{
    switch (action.type){
        case 'GET_POKEMONS':
            return{
                ...state,
                pokemons:action.payload
            }
        case 'FILTER_CREATED':
            const allpokemos= state.pokemons
            const createdFilter= action.payload==='created'? allpokemos.filter(el=>el.createdInDb): allpokemos.filter(el=> !el.createdInDb)

            return{
                ...state,
                pokemons:action.payload==='todos'?allpokemos:createdFilter
            }
        default:
        return state
    }
}

export default rootReducer;
