import React, { Component } from 'react';
import { connect } from 'react-redux'
import { useState,useEffect } from 'react';
import { useDispatch,useSelector } from 'react-redux';
import { getPokemons, getPokemonsTypes } from '../actions';
import {Link} from 'react-router-dom'
import Card from './Card'
import Paginado from './Paginado';


export default function Home(){
    const dispatch= useDispatch()
    const allPokemons=useSelector((state)=>state.pokemons)
    const [currentPage,setCurrentPage]=useState(1)
    const [PokemonPerPage,setPokemonPerPage]=useState(12)
    const lastPokemonPage= currentPage*PokemonPerPage //12
    const firstPokemonPage=lastPokemonPage-PokemonPerPage //0
    const currentPokemon= allPokemons.slice(lastPokemonPage,firstPokemonPage)

    const paginado=(pageNumber)=>{
        setCurrentPage(pageNumber)
    }


    useEffect(()=>{
        dispatch(getPokemons())
    },[dispatch])

    function handleClick(e){
        e.preventDefault();
        dispatch(getPokemons())
    }

    return(
        <div>
            <Link to ='/pokemons'>Crear Pokemon</Link>
            <h1>POKEMONS</h1>
            <button onClick={e=>{handleClick(e)}}>
                Volver a cargar todos los pokemones
            </button>
            <div>
                <select>
                    <option value='asc'>Ascendente</option>
                    <option value='des'>Descendente</option>
                </select>
                <select>
                    <option value="todo">Todos</option>
                    <option value="crea">Creados</option>
                    <option value="exis">Existente</option>
                </select>
                <Paginado
                
                />
                {
                    allPokemons?.map((c)=>{ 
                        return(
                            
                            <Card  
                            key={c.id} 
                            name={c.name} 
                            image={c.image} 
                            type={c.types}/> 
                        )
                    })}
            </div>
        </div>
    )
}
