import axios from 'axios';


export function getPokemons(){
    return async function(dispatch){
        axios.get('http://localhost:3001/pokemons')
        .then((res)=>res.json())
        .then((json)=>{dispatch({type:'GET_POKEMONS',payload:json})})
        // return dispatch({
        //  type: 'GET_POKEMONS',
        //  payload:json.data
        // })
    }
}
