import React from "react";
import {Link} from 'react-router-dom'
import { useDispatch,useSelector } from "react-redux";
import { pokemonDetail } from "../actions/index";
import { useEffect } from "react";


export default function PokemonDetail(props){
    const dispatch=useDispatch()

    useEffect(()=>{
        dispatch(pokemonDetail(props.match.params.id))
    },[dispatch])

    const myPokemon=useSelector((state)=>state.detail)

    return(
        <div>
            {
                myPokemon.length>0 ? 
                <div>
                    <h1>{myPokemon[0].name}</h1>
                    <img src={myPokemon[0].img?myPokemon[0].img:myPokemon[0].image} alt='imagen no encontrada'/>
                    <h4>{!myPokemon[0].createInDb?myPokemon[0].type + ' ': myPokemon[0].type.map(el=>el.name + (' '))}</h4>
                    <h5>Vida: {myPokemon[0].hp}</h5>
                    <h5>Ataque: {myPokemon[0].attack}</h5>
                    <h5>Velocidad: {myPokemon[0].speed}</h5>
                    <h5>Altura: {myPokemon[0].height}</h5>
                    <h5>Peso: {myPokemon[0].weight}</h5>

                </div>
                : <p>Loading...</p>
            }
            <Link to ='/home'>
                <button>Volver</button>
            </Link>
        </div>
    )

    
}
