import React, { Component } from 'react';
import { connect } from 'react-redux'
import { useState,useEffect } from 'react';
import { useDispatch,useSelector } from 'react-redux';
import { filterByCreated, getPokemons, getPokemonsTypes, orderByName } from '../actions';
import {Link, Redirect} from 'react-router-dom'
import Card from './Card'
import Paginado from './Paginado';


export default function Home(){
    const dispatch= useDispatch()
    const allPokemons=useSelector((state)=>state.pokemons)
    const [orden,setOrden]=useState
    const [currentPage,setCurrentPage]=useState(1)
    const [PokemonPerPage,setPokemonPerPage]=useState(12)
    const lastPokemonPage= currentPage*PokemonPerPage //12
    const firstPokemonPage=lastPokemonPage-PokemonPerPage //0
    const currentPokemon= allPokemons.slice(firstPokemonPage,lastPokemonPage)

    const paginado=(pageNumber)=>{
        
        setCurrentPage(pageNumber)
        
    }


    useEffect(()=>{
        dispatch(getPokemons())
    },[dispatch])

    function handleClick(e){
        e.preventDefault();
        dispatch(getPokemons())
    }
    function handleCreated(e){
        e.preventDefault();
        dispatch(filterByCreated(e.target.value))
    }
    function handleOrder(e){
        e.preventDefault();
        dispatch(orderByName(e.target.value))
        setCurrentPage(1)
        
    }

    return(
        <div id='Paginas'>
            <Link to ='/pokemons'>Crear Pokemon</Link>
            <h1>POKEMONS</h1>
            <button onClick={e=>{handleClick(e)}}>
                Volver a cargar todos los pokemones
            </button>
            <div>
                <select onChange={e=>handleOrder(e)}>
                    <option value='asc'>Ascendente</option>
                    <option value='des'>Descendente</option>
                </select>
                <select onChange={e=>handleCreated(e)}>
                    <option value="todos">Todos</option>
                    <option value="created">Creados</option>
                    <option value="existente">Existente</option>
                </select>
                <select>
                    <option value="maymen">Mayor poder de ataque</option>
                    <option value="menmay">Menor poder de ataque</option>

                </select>
                {
                    currentPokemon?.map((c)=>{ 
                        return(
                            
                            <Card  
                            key={c.id} 
                            name={c.name} 
                            image={c.image} 
                            type={c.types}/> 
                            )
                        })}
                    <Paginado
                    PokemonPerPage={PokemonPerPage}
                    allPokemons={allPokemons.length}
                    paginado={paginado}
                    />
            </div>
        </div>
    )
}
